import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { MessageCircle, X } from 'lucide-react'; 

export default function FloatingChatbot() {
  // Keeping the state management for future use, but setting it to 'true' 
  // initially or always showing it, based on your current need to debug the open window.
  // For standard deployment, you should keep useState(false).
  const [isOpen, setIsOpen] = useState(true); // <-- Set to true to always show for debugging/development
  const chatbotUrl = '/chatbot.html'; 
  
  const toggleChat = () => setIsOpen(!isOpen);

  // If you want the chat to always be open (like in your screenshot), 
  // you can remove the toggleChat function and setIsOpen state completely.

  return (
    // Outer container: Fixed position at bottom-4 right-4
    // Use a slightly larger size for a better chat experience (e.g., w-96 h-[500px])
    <div className="fixed bottom-4 right-4 z-50">
      
      {/* Chat Window (Appears when open) */}
      <AnimatePresence>
        {isOpen && (
          <motion.div
            // CHANGE 1: Use a specific height (e.g., h-[500px]) and width (w-96) 
            // for reliable sizing of the chat window. 
            className="w-96 h-[500px] bg-white rounded-lg shadow-2xl border border-gray-200 overflow-hidden"
            initial={{ opacity: 0, scale: 0.8, y: 50 }}
            animate={{ opacity: 1, scale: 1, y: 0 }}
            exit={{ opacity: 0, scale: 0.8, y: 50 }}
            transition={{ type: "spring", stiffness: 400, damping: 30 }}
          >
            {/* Header with Title and Close Button */}
            <div className="flex justify-between items-center p-3 bg-indigo-600 text-white shadow-md">
              <h3 className="text-lg font-semibold">VitaBot Chat</h3>
              <button 
                onClick={toggleChat}
                className="p-1 rounded-full hover:bg-indigo-700 transition"
                aria-label="Close Chat"
              >
                <X size={20} />
              </button>
            </div>

            {/* Iframe with Chat Content */}
            <iframe
              src={chatbotUrl}
              title="VitaBot Chatbot"
              // CHANGE 2: Ensure iframe is w-full and h-full to completely fill 
              // the remaining vertical space after the header.
              // Use flex-1 with flex-col on the parent motion.div for dynamic height,
              // OR stick to w-full h-full within the fixed-size container.
              className="w-full h-full" 
              style={{ border: 'none' }}
            >
              Your browser does not support iframes.
            </iframe>

          </motion.div>
        )}
      </AnimatePresence>

      {/* REMOVED: The floating toggle button that generates the Plus/Message icon.
          If the chat is meant to be always visible (as in your image), 
          we rely only on the close button in the header. 
      */}
      {/* {!isOpen && (
          <button
            onClick={toggleChat}
            className="w-14 h-14 rounded-full shadow-xl text-white flex items-center justify-center bg-pink-600 hover:bg-pink-700"
            aria-label="Open Chatbot"
          >
            <MessageCircle size={24} />
          </button>
      )}
      */}
    </div>
  );
}
