import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
// Assuming you have an Icon component for the chat bubble. 
// You might need to install 'react-icons' or use a simple SVG.
import { MessageCircle, X } from 'lucide-react'; // Example icons

export default function FloatingChatbot() {
  const [isOpen, setIsOpen] = useState(false);
  const chatbotUrl = '/chatbot.html'; 
  
  const toggleChat = () => setIsOpen(!isOpen);

  return (
    // Outer container: Fixed position at bottom-right of the viewport
    <div className="fixed bottom-4 right-4 z-50">
      
      {/* Chat Window (Appears when open) */}
      <AnimatePresence>
        {isOpen && (
          <motion.div
            className="w-80 h-96 bg-white rounded-lg shadow-2xl border border-gray-200 overflow-hidden mb-2"
            initial={{ opacity: 0, scale: 0.8, y: 50 }}
            animate={{ opacity: 1, scale: 1, y: 0 }}
            exit={{ opacity: 0, scale: 0.8, y: 50 }}
            transition={{ type: "spring", stiffness: 400, damping: 30 }}
          >
            {/* Header with Title and Close Button */}
            <div className="flex justify-between items-center p-3 bg-indigo-600 text-white shadow-md">
              <h3 className="text-lg font-semibold">VitaBot Chat</h3>
              <button 
                onClick={toggleChat}
                className="p-1 rounded-full hover:bg-indigo-700 transition"
              >
                <X size={20} />
              </button>
            </div>

            {/* Iframe with Chat Content */}
            {/* The iframe is now sized reliably within the fixed 80x96 container */}
            <iframe
              src={chatbotUrl}
              title="VitaBot Chatbot"
              className="w-full h-full"
              style={{ border: 'none' }}
            >
              Your browser does not support iframes.
            </iframe>

          </motion.div>
        )}
      </AnimatePresence>

      {/* Floating Action Button (FAB) */}
      <button
        onClick={toggleChat}
        className={`
          w-14 h-14 rounded-full shadow-xl text-white 
          flex items-center justify-center transition-all duration-300
          ${isOpen ? 'bg-gray-500 hover:bg-gray-600 rotate-45' : 'bg-pink-600 hover:bg-pink-700'}
        `}
        aria-label={isOpen ? "Close Chatbot" : "Open Chatbot"}
      >
        {/* Toggle between a Chat icon and an X icon */}
        {isOpen ? <X size={24} /> : <MessageCircle size={24} />}
      </button>
    </div>
  );
}
