import React from 'react';
import { motion } from 'framer-motion';

/**
 * ChatbotPage component.
 * Ensures the embedded chatbot is fully visible without clipping the corners.
 */
export default function ChatbotPage() {
  const chatbotUrl = '/chatbot.html'; 

  return (
    <motion.div
      // MODIFICATION 1: Change p-6 to p-4, but keep p-4 for general padding.
      // The key change is ensuring this container takes up all available vertical space.
      // Use min-h-screen or h-full depending on your main layout. Assuming the parent
      // component (App/Layout) is already ensuring full height.
      className="w-full h-full p-4 flex flex-col items-center bg-gray-50"
      // Remove inline style that overrides padding-top
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <h2 className="text-3xl font-bold text-indigo-700 mb-2 mt-4">
        Chat with VitaBot ðŸ¤–
      </h2>
      <p className="text-gray-600 mb-4">
        Ask any questions about vitamins, health, or deficiencies.
      </p>
      
      {/* The Iframe to embed the HTML file */}
      <iframe
        src={chatbotUrl}
        title="VitaBot Chatbot"
        // MODIFICATION 2: Use flex-grow (or flex-1) to make the iframe take up 
        // the remaining vertical space within the flex container (the parent motion.div).
        // Set height to 'auto' to let flex control it, or '100%' if necessary, but
        // 'flex-1' is the most effective Tailwind class for this.
        className="w-full max-w-5xl mt-2 border-2 border-indigo-200 rounded-lg shadow-xl overflow-hidden flex-1 min-h-0"
        // Set height to 'auto' and remove '85vh'
        // 'min-h-0' is crucial here for flex-1 to work correctly in limiting its size.
        style={{ border: 'none' }} 
      >
        Your browser does not support iframes. Please open the chatbot directly.
      </iframe>
    </motion.div>
  );
}
