import React from 'react';
import { motion } from 'framer-motion';

/**
 * ChatbotPage component.
 * Ensures the embedded chatbot is fully visible and correctly sized.
 */
export default function ChatbotPage() {
  const chatbotUrl = '/chatbot.html'; 

  return (
    <motion.div
      // Ensure the container fills the height (h-full) of its parent.
      // Use 'p-4' for padding and 'flex flex-col items-center' for horizontal centering.
      // Adding 'justify-center' to vertically center the entire content block.
      className="w-full h-full p-4 flex flex-col items-center justify-center bg-gray-50"
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <h2 className="text-3xl font-bold text-indigo-700 mb-2 mt-4">
        Chat with VitaBot ðŸ¤–
      </h2>
      <p className="text-gray-600 mb-4">
        Ask any questions about vitamins, health, or deficiencies.
      </p>
      
      {/* The Iframe to embed the HTML file */}
      <iframe
        src={chatbotUrl}
        title="VitaBot Chatbot"
        // CHANGE: Use max-w-4xl for a balanced width.
        // flex-1 and min-h-0 remain to ensure it uses all available vertical space.
        className="w-full max-w-4xl mt-2 border-2 border-indigo-200 rounded-lg shadow-xl overflow-hidden flex-1 min-h-0"
        style={{ border: 'none', height: '65vh' }} 
        // Note: Using a fixed vh here might be safer if the parent container 
        // (the main App layout) is not correctly setting 'h-full' or 'h-screen'. 
        // I've temporarily set it back to a fixed height (65vh) to give it a 
        // guaranteed size, as the 'flex-1' approach may fail if the parent isn't
        // sized correctly.
      >
        Your browser does not support iframes. Please open the chatbot directly.
      </iframe>
    </motion.div>
  );
}
