import React from 'react';
import { motion } from 'framer-motion';

/**
 * ChatbotPage component.
 */
export default function ChatbotPage() {
  const chatbotUrl = '/chatbot.html'; 

  return (
    <motion.div
      // Ensure the container fills the height (h-full) and add bottom padding (pb-4)
      // to give the iframe more room vertically before hitting the bottom edge.
      className="w-full h-full p-4 pb-4 flex flex-col items-center bg-gray-50"
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <h2 className="text-3xl font-bold text-indigo-700 mb-2 mt-4">
        Chat with VitaBot ðŸ¤–
      </h2>
      <p className="text-gray-600 mb-4">
        Ask any questions about vitamins, health, or deficiencies.
      </p>
      
      {/* The Iframe to embed the HTML file */}
      <iframe
        src={chatbotUrl}
        title="VitaBot Chatbot"
        // CHANGE 1: **Reduce max-width** from max-w-5xl (approx. 64rem) to 
        // max-w-3xl (approx. 48rem) for a better chat window look.
        // flex-1 and min-h-0 remain to ensure it uses all available vertical space.
        className="w-full max-w-3xl mt-2 border-2 border-indigo-200 rounded-lg shadow-xl overflow-hidden flex-1 min-h-0"
        style={{ border: 'none' }} 
      >
        Your browser does not support iframes. Please open the chatbot directly.
      </iframe>
    </motion.div>
  );
}
