import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { X } from 'lucide-react'; 

export default function FloatingChatbot() {
  // Set to false for production, but keeping it true here to see the window open
  const [isOpen, setIsOpen] = useState(true); 
  const chatbotUrl = '/chatbot.html'; 
  
  const toggleChat = () => setIsOpen(!isOpen);

  return (
    // Outer container: Fixed position at bottom-4 right-4
    <div className="fixed bottom-4 right-4 z-50">
      
      {/* Chat Window (Appears when open) */}
      <AnimatePresence>
        {isOpen && (
          <motion.div
            // Final Height Adjustment: Increased height to ensure typing area is visible.
            // Width remains w-96 for a clean look.
            className="w-96 h-[550px] bg-white rounded-lg shadow-2xl border border-gray-200 overflow-hidden"
            initial={{ opacity: 0, scale: 0.8, y: 50 }}
            animate={{ opacity: 1, scale: 1, y: 0 }}
            exit={{ opacity: 0, scale: 0.8, y: 50 }}
            transition={{ type: "spring", stiffness: 400, damping: 30 }}
          >
            {/* Header with Title and Close Button */}
            {/* Adjusted padding: p-2 instead of p-3 for more vertical space */}
            <div className="flex justify-between items-center p-2 bg-indigo-600 text-white shadow-md">
              <h3 className="text-lg font-semibold">VitaBot Chat</h3>
              <button 
                onClick={toggleChat}
                className="p-1 rounded-full hover:bg-indigo-700 transition"
                aria-label="Close Chat"
              >
                <X size={20} />
              </button>
            </div>

            {/* Iframe with Chat Content */}
            <iframe
              src={chatbotUrl}
              title="VitaBot Chatbot"
              // Ensure the iframe fills the remaining space completely
              className="w-full h-full" 
              style={{ border: 'none' }}
            >
              Your browser does not support iframes.
            </iframe>

          </motion.div>
        )}
      </AnimatePresence>

      {/* Floating Toggle Button (Hidden while chat is open) */}
      {!isOpen && (
          <button
            onClick={toggleChat}
            className="w-14 h-14 rounded-full shadow-xl text-white flex items-center justify-center bg-pink-600 hover:bg-pink-700"
            aria-label="Open Chatbot"
          >
            {/* Use the MessageCircle icon when closed */}
            <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="lucide lucide-message-circle"><path d="m3 21 1.9-5.7a8.5 8.5 0 1 1 3.8 3.8z"/></svg>
          </button>
      )}
    </div>
  );
}
